'use strict';

const RequestCounter = require('./RequestCounter');

/**
 * Limiter
 */
class Limiter {

  /**
   * @param {object} config Object of values
   * @return empty
   */
  constructor(config) {
    let limit = null;
    let period = null;
    let increment = null;

    if (config != null) {
      limit     = config.limit;
      period    = config.period;
      increment = config.increment;
    }

    this._config = { limit, period, increment };
    this._requestCounters = {};
  }

  /**
   * @param {key} key The identifier for the request
   * @return empty
   */
  _errorCheckForKey(key) {
    if (key == null) {
      throw new Error('Error: key is required.');
    }
  }

  /**
   * @param {string} key The identifier for the request
   * @return empty
   */
  _createRequestCounter(key) {
    this._errorCheckForKey(key);

    if (this._requestCounters[key] == null) {
      const limit = this._config.limit;
      const period = this._config.period;

      this._requestCounters[key] = new RequestCounter({ limit, period });
    }
  }

  /**
   * @param {string} key The identifier for the request
   * @return {integer} Remaining request count
   */
  addRequest(key) {
    this._errorCheckForKey(key);
    this._createRequestCounter(key);
    this._requestCounters[key].add();

    return this._requestCounters[key].getRemaining();
  }

  /**
   * @param {string} key The identifier for the request
   * @return {integer} Remaining request count
   */
  getRemainingRequests(key) {
    this._errorCheckForKey(key);
    this._createRequestCounter(key);

    return this._requestCounters[key].getRemaining();
  }

}
module.exports = Limiter;

'use strict';

const DEFAULT_LIMIT     = 25;
const DEFAULT_PERIOD    = 500;
const DEFAULT_INCREMENT = 1;

/**
 * RequestCounter
 */
class RequestCounter {

  /**
   * @param {object} config Object of values
   * @return {object} An instance of the request counter object
   */
  constructor(config) {
    let limit     = DEFAULT_LIMIT;
    let period    = DEFAULT_PERIOD;
    let increment = DEFAULT_INCREMENT;

    if (config != null) {
      if (config.limit != null) {
        limit = config.limit;
      }
      if (config.period != null) {
        period = config.period;
      }
      if (config.increment != null) {
        increment = config.increment;
      }
    }

    this._maxLimit  = this._limit             = limit;
    this._period    = period;
    this._increment = increment;
    this._startTime = (new Date()).valueOf();

    this.interval = setInterval(() => {
      this.incrementLimit();
    }, period);

    return this;
  }

  /**
   * @return empty
   */
  destroy() {
    clearInterval(this.interval);
    this.interval = null;
  }

  /**
   * @return empty
   */
  incrementLimit() {
    if (this._limit < this._maxLimit) {
      this._limit += this._increment;
    }
  }

  /**
   * @return {int} The remaining available requests for the identifier
   */
  add() {
    if (this._limit > 0) {
      this._limit--;
    }

    return this._limit; 
  }

  /**
   * @return {int} The remaining available requests for the identifier
   */
  getRemaining() {
    return this._limit;
  }

};

module.exports = RequestCounter;

'use strict';
const chai       = require('chai');
const sinon      = require('sinon');
const sinonChai  = require('sinon-chai');

chai.use(sinonChai);

const expect = chai.expect;

describe('Limiter', () => {
  let Limiter;
  let limiter;
  let config;
  let key;

  beforeEach(() => {
    config = {
      limit     : 20,
      period    : 5000,
      increment : 2,
    };
    Limiter = require('../src/Limiter.js');
  });

  describe('#constructor', () => {
    it('should not error if not given a config', () => {
      expect(() => { return new Limiter(); }).not.to.throw(Error);
    });

    describe('without config', () => {

      beforeEach(() => {
        limiter = new Limiter();
      });

      it('should default config.limit to null', () => {
        expect(limiter._config.limit).to.not.exist;
      });

      it('should default config.period to null', () => {
        expect(limiter._config.period).to.not.exist;
      });

      it('should default config.increment to null', () => {
        expect(limiter._config.increment).to.not.exist;
      });

      it('should create _requestCounters', () => {
        expect(limiter._requestCounters).to.exist;
      });

    });

    describe('with config', () => {

      beforeEach(() => {
        config = {
          limit     : 200,
          period    : 8000,
          increment : 2,
        };
        limiter = new Limiter(config);
      });

      it('should assign given period to config.limit', () => {
        expect(limiter._config.limit).to.equal(config.limit);
      });

      it('should assign given period to config.period', () => {
        expect(limiter._config.period).to.equal(config.period);
      });

      it('should assign given period to config.increment', () => {
        expect(limiter._config.increment).to.equal(config.increment);
      });

      it('should create _requestCounters', () => {
        expect(limiter._requestCounters).to.exist;
      });

    });

  });

  describe('#_errorCheckForKey', () => {

    it('should error if no key is given', () => {
      expect(() => { limiter._errorCheckForKey(); }).to.throw(Error);
    });

    it('should do nothing if a key is given', () => {
      expect(() => { limiter._errorCheckForKey('anything'); }).to.not.throw(Error);
    });

  });

  describe('#_createRequestCounter', () => {

    beforeEach(() => {
      limiter = new Limiter(config);
      key = 'test';
    });

    it('should call _errorCheckForKey', () => {
      sinon.spy(limiter, '_errorCheckForKey');
      limiter.getRemainingRequests(key);
      expect(limiter._errorCheckForKey).to.have.been.called;
    });

    it('should register a new RequestCounter model if one doesn\'t exist', () => {
      limiter._createRequestCounter(key);

      expect(limiter._requestCounters[key]).to.exist;
    });

    it('should use the same RequestCounter instance with the same key', () => {
      limiter._createRequestCounter(key);
      limiter._requestCounters[key]._limit = config.limit - 2;
      limiter._createRequestCounter(key);

      expect(limiter._requestCounters[key]._limit).to.equal(config.limit - 2);
    });

  });

  describe('#addRequest', () => {

    beforeEach(() => {
      limiter = new Limiter(config);
      key = 'test';
    });

    it('should call _errorCheckForKey', () => {
      sinon.spy(limiter, '_errorCheckForKey');
      limiter.getRemainingRequests(key);
      expect(limiter._errorCheckForKey).to.have.been.called;
    });

    it('should call _createRequestCounter', () => {
      sinon.spy(limiter, '_createRequestCounter');
      limiter.getRemainingRequests(key);
      expect(limiter._createRequestCounter).to.have.been.called;
    });

    it('should return the altered limit after a request', () => {
      let result;
      result = limiter.addRequest(key);
      expect(result).to.equal(config.limit - 1);

      limiter.addRequest(key);
      result = limiter.addRequest(key);
      expect(result).to.equal(config.limit - 3);
    });

  });

  describe('#getRemainingRequests', () => {

    describe('when request within limits', () => {

      beforeEach(() => {
        limiter = new Limiter(config);
        key = 'test';
      });

      it('should call _errorCheckForKey', () => {
        sinon.spy(limiter, '_errorCheckForKey');
        limiter.getRemainingRequests(key);
        expect(limiter._errorCheckForKey).to.have.been.called;
      });

      it('should call _createRequestCounter', () => {
        sinon.spy(limiter, '_createRequestCounter');
        limiter.getRemainingRequests(key);
        expect(limiter._createRequestCounter).to.have.been.called;
      });

      it('should return limit minus the number of requests', () => {
        let request;
        request = limiter.getRemainingRequests(key);
        expect(request).to.equal(config.limit);

        limiter.addRequest(key);
        limiter.addRequest(key);
        request = limiter.getRemainingRequests(key);
        expect(request).to.equal(config.limit - 2);
      });

    });

    describe('when request outside of limits', () => {

      beforeEach(() => {
        limiter = new Limiter({
          limit  : 0,
          period : 0,
        });
        key = 'test';
        limiter.addRequest(key);
        limiter._requestCounters[key]._limit = 0;
      });

      it('should return 0 if request limit is reached', () => {
        expect(limiter.getRemainingRequests(key)).to.equal(0);
      });

    });

  });

});

'use strict';
const chai = require('chai');
const sinon = require('sinon');
const sinonChai = require('sinon-chai');
const START_TIME = 140000;

chai.use(sinonChai);

const expect = chai.expect;

describe('RequestCounter', () => {
  let RequestCounter;
  let config;
  let clock;
  let requestCounter;

  beforeEach(() => {
    RequestCounter = require('../src/RequestCounter.js');
    config = {
      period    : 500,
      limit     : 20,
      increment : 1,
    };
    clock = sinon.useFakeTimers(START_TIME);
  });

  afterEach(() => {
    clock.restore();
  });

  describe('#constructor', () => {

    it('should be able to init without a config', () => {
      expect(new RequestCounter).not.to.throw;
    });

    describe('without config', () => {

      beforeEach(() => {
        requestCounter = new RequestCounter();
        sinon.spy(requestCounter, 'incrementLimit');
      });

      afterEach(() => {
        requestCounter.destroy();
      });

      it('should return an object', () => {
        expect(typeof(requestCounter)).to.equal('object');
      });

      it('should create maxLimit', () => {
        expect(requestCounter._maxLimit).to.exist;
      });

      it('should create limit', () => {
        expect(requestCounter._limit).to.exist;
      });

      it('should create a period', () => {
        expect(requestCounter._period).to.exist;
      });

      it('should create a increment', () => {
        expect(requestCounter._increment).to.exist;
      });

      it('should create a startTime', () => {
        // TODO: can check the time is accurate?
        expect(requestCounter._startTime).to.exist;
      });

      it('should set up an interval calling incrementLimit', () => {
        clock.tick(config.period + 1);
        expect(requestCounter.incrementLimit).to.have.been.called;
      });

    });

    describe('with config', () => {

      beforeEach(() => {
        requestCounter = new RequestCounter(config);
        sinon.spy(requestCounter, 'incrementLimit');
      });

      afterEach(() => {
        requestCounter.destroy();
      });

      it('should return an object', () => {
        expect(typeof(requestCounter)).to.equal('object');
      });

      it('should create maxLimit', () => {
        expect(requestCounter._maxLimit).to.exist;
      });

      it('should create limit', () => {
        expect(requestCounter._limit).to.exist;
      });

      it('should create a period', () => {
        expect(requestCounter._period).to.exist;
      });

      it('should create a increment', () => {
        expect(requestCounter._increment).to.exist;
      });

      it('should create a startTime', () => {
        // TODO: can check the time is accurate?
        expect(requestCounter._startTime).to.exist;
      });

      it('should set up an interval calling incrementLimit', () => {
        clock.tick(config.period + 1);
        expect(requestCounter.incrementLimit).to.have.been.calledOnce;

        clock.tick(config.period);
        expect(requestCounter.incrementLimit).to.have.been.calledTwice;
      });

    });

  });

  describe('#destroy', () => {

    beforeEach(() => {
      requestCounter = new RequestCounter();
      requestCounter.destroy();
    });

    it('should destroy the interval', () => {
      expect(requestCounter.interval).to.not.exist;
    });

  });

  describe('#incrementLimit', () => {

    beforeEach(() => {
      requestCounter = new RequestCounter(config);
    });

    afterEach(() => {
      requestCounter.destroy();
    });

    it('should increment _currentLimit if not at _maxLimit', () => {
      requestCounter._limit = 0;
      requestCounter.incrementLimit();
      expect(requestCounter._limit).to.equal(1);
    });

    it('should not increment _currentLimit past _maxLimit', () => {
      requestCounter.incrementLimit();
      expect(requestCounter._limit).to.equal(config.limit);
    });

  });

  describe('#add', () => {

    beforeEach(() => {
      requestCounter = new RequestCounter(config);
    });

    afterEach(() => {
      requestCounter.destroy();
    });

    it('should decrement limit when below limit', () => {
      requestCounter.add();
      expect(requestCounter._limit).to.equal(config.limit - 1);
    });

    it('should not decrement limit if already at limit', () => {
      requestCounter._limit = 0;
      requestCounter.add();
      expect(requestCounter._limit).to.equal(0);
    });

    it('should reset at the end of the period', () => {
      requestCounter.add();
      clock.tick(config.period + 1);
      expect(requestCounter._limit).to.equal(config.limit);
    });

  });

  describe('#getRemaining', () => {

    beforeEach(() => {
      requestCounter = new RequestCounter(config);
    });

    afterEach(() => {
      requestCounter.destroy();
    });

    it('should return the limit', () => {
      expect(requestCounter.getRemaining()).to.equal(requestCounter._limit);
    });

  });

});
