'use strict';

const TokenBucket = require('./TokenBucket');
const _           = require('lodash');

/**
 * RateLimiter Class
 *
 * Handles creation and destruction of TokenBuckets by key.
 */
class RateLimiter {

  /**
   * Creates a RateLimiter object.
   *
   * @param {object} config configuration values
   * @property {integer} limit The max amount of tokens per period.
   * @property {integer} period The time in ms before tokens will increment.
   * @property {integer} increment The amount of tokens that will increment each period.
   */
  constructor(config) {
    let limit     = null;
    let period    = null;
    let increment = null;

    if (config != null) {
      limit     = config.limit;
      period    = config.period;
      increment = config.increment;
    }

    this.config = { limit, period, increment };
    this.tokenBuckets = {};
  }

  /**
   * Cleans up each TokenBucket and nulls the array.
   */
  destroy() {
    _.each(this.tokenBuckets, (value, key) => {
      this.tokenBuckets[key].destroy();
      delete this.tokenBuckets[key];
    });
  }

  /**
   * Helper to throw an error if the key is invalid.
   *
   * @param {key} key The identifier for the request
   * @throws {Error} throw error when key is invalid
   * @private
   */
  _errorCheckForKey(key) {
    if (key == null || typeof(key) != 'string') {
      throw new Error('Error: key is required.');
    }
  }

  /**
   * Helper to create a new TokenBucket if needed.
   *
   * @param {string} key The identifier for the request
   * @private
   */
  _createTokenBucket(key) {
    this._errorCheckForKey(key);

    if (this.tokenBuckets[key] == null) {
      const limit = this.config.limit;
      const period = this.config.period;

      this.tokenBuckets[key] = new TokenBucket({ limit, period });
    }
  }

  /**
   * Appropriately decrements the remaining token availability
   * when called and returns the remainder.
   *
   * @param {string} key The identifier for the request
   * @param {integer} [amount] The number to decrement by
   * @return {integer} Remaining request count
   */
  decrementTokens(key, amount) {
    this._errorCheckForKey(key);
    this._createTokenBucket(key);
    this.tokenBuckets[key].decrementTokens(amount);

    return this.tokenBuckets[key].getTokensRemaining();
  }

  /**
   * Returns the remaining request count for the given key.
   * If the key doesn't already exist, it will create it.
   *
   * @param {string} key The identifier for the request
   * @return {integer} Remaining request count
   */
  getTokensRemaining(key) {
    this._errorCheckForKey(key);
    this._createTokenBucket(key);

    return this.tokenBuckets[key].getTokensRemaining();
  }

}

module.exports = RateLimiter;

'use strict';

const DEFAULT_LIMIT     = 25;
const DEFAULT_PERIOD    = 5000;
const DEFAULT_INCREMENT = 1;

/**
 * TokenBucket
 *
 * Handles how many tokens are available at any given time based on
 * the configuration set and the token bucket algorithm.
 *
 * @see https://en.wikipedia.org/wiki/Token_bucket
 */
class TokenBucket {

  /**
   * Creates a TokenBucket object.
   *
   * @param {object} config configuration values
   * @property {integer} [limit=25] The max amount of tokens per period.
   * @property {integer} [period=5000] The time in ms before tokens will increment.
   * @property {integer} [increment=1] The amount of tokens that will increment each period.
   */
  constructor(config) {
    config = config || {};

    const period    = config.period || DEFAULT_PERIOD;

    this.limit = this.tokens = config.limit || DEFAULT_LIMIT;
    this.increment = config.increment || DEFAULT_INCREMENT;

    this._interval = setInterval(() => {
      this._incrementTokens();
    }, period);
  }

  /**
   * Cleans up the interval set by the constructor.
   */
  destroy() {
    clearInterval(this._interval);
    this._interval = null;
  }

  /**
   * Helper to increment tokens on the interval.
   *
   * @private
   */
  _incrementTokens() {
    if (this.tokens < this.limit) {
      this.tokens += this.increment;
    }
  }

  /**
   * Decrements the amount of tokens available based on amount
   * and returns the value.
   *
   * @param {integer} [amount=1] The number to decrement by
   * @return {integer} The remaining available requests for the identifier
   */
  decrementTokens(amount) {
    amount = amount || 1;

    if (this.tokens > 0) {
      this.tokens -= amount;
    }

    return this.tokens; 
  }

  /**
   * Returns the currently available tokens.
   *
   * @return {integer} The remaining available requests for the identifier
   */
  getTokensRemaining() {
    return this.tokens;
  }

};

